import React, { Component } from "react";
import ReactPlayer from "react-player";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

import Title from "../Title";
import AllMovies from "../../Data/AllMovies";
import "../../styles/Abstract.css";

class Browse extends React.Component {
  constructor(props) {
    super(props);
    this.state = { movies: {} };
  }

  //make sure to make the api call before the component is rendered. Use lifecycle hook
  componentWillMount() {
    this.setState(this.getData());
  }

  //function to make api call. For now uses dummy data
  getData() {
    //can use fetch() then promise chaining
    return AllMovies;
  }

  render() {
    return (
      <div>
        <Title text="Movies database"></Title>


        <div className="Body">
        <div className="search">
          <Form>
            <Form.Group controlId="formBasicSearch">
              <Form.Label>Search for a movie</Form.Label>
              <Form.Control placeholder="Enter Search" /> 
            </Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </div>
          <div className="introduction">
            <h2>Movie list</h2>
          </div>
        </div>
      </div>
    );
  }
}

export default Browse;
