import React, { Component } from "react";
import ReactPlayer from "react-player";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

import Title from "../Title";
import AllMovies from "../../Data/AllMovies";
import "../../styles/Abstract.css";

class Browse extends React.Component {
  constructor(props) {
    super(props);
    this.state = { movies: {} };
  }

  //make sure to make the api call before the component is rendered. Use lifecycle hook
  componentWillMount() {}

  //function to make api call. For now uses dummy data
  getData() {
    //can use fetch() then promise chaining
    return AllMovies;
  }

  render() {
    return (
      <div>
        <Title text="Movies database"></Title>
        <div className="search">
          <Form>
            <Form.Group controlId="formBasicSearch">
              <Form.Label>Search for a movie</Form.Label>
              <Form.Control placeholder="Enter email" />
              <Form.Text className="text-muted">
                We'll never share your email with anyone else.
              </Form.Text>
            </Form.Group>

            <Form.Group controlId="formBasicPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control type="password" placeholder="Password" />
            </Form.Group>
            <Form.Group controlId="formBasicCheckbox">
              <Form.Check type="checkbox" label="Check me out" />
            </Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </div>

        <div className="Body">
          <div className="introduction">
            <h2>Movie list</h2>
          </div>
        </div>
      </div>
    );
  }
}

export default Browse;
